      width: 100%;
      @include breakpoint("desktop") {
        flex: 1;
      }
    }

    .desc__container {
      padding: 3rem 1.5rem;
      text-align: center;
      height: auto;

      @include breakpoint("desktop") {
        padding: 0;
        flex: 1;
        display: grid;
        place-items: center;
      }

      .desc {
        @include breakpoint("desktop") {
          text-align: left;
          padding: 0 8rem;
        }

        h1 {
          font-family: "Fraunces", serif;
          font-size: 2rem;
          color: $very-dark-desaturated-blue;
          margin-bottom: 3rem;

          @include breakpoint("desktop") {
            font-size: 3rem;
          }
        }

        p {
          color: $grayish-blue;
          line-height: 1.6;
          margin-bottom: 2rem;
        }

        .learn__more {
          position: relative;
          display: inline;
          font-family: "Fraunces", serif;
          color: $very-dark-desaturated-blue;
          cursor: pointer;

          &::before {
            content: "";
            position: absolute;
            left: -3px;
            right: 0;
            margin: auto;
            top: 14px;
            display: flex;
            height: 6px;
            width: 7rem;
            background-color: rgb(238, 238, 148);
            border-radius: 4px;
            z-index: -1;
          }

          &__2 {
            &::before {
              background-color: #fdb1a7;
            }
          }
        }
      }
    }
  }
  .services {
    position: relative;
    margin-top: -5px;

    .content {
      position: absolute;
      bottom: 4rem;
      display: flex;
      flex-direction: column;
      align-items: center;
      padding: 0 1rem;

      h1 {
        font-family: "Fraunces", serif;
        margin-bottom: 2rem;
        color: $dark-desaturated-cyan;
        font-size: 2rem;
      }
      p {
        text-align: center;
        line-height: 1.4;
        color: $dark-desaturated-cyan;
      }

      .phtg {
        color: $dark-blue;
      }
    }
  }

  .reverse {
    flex-direction: row;
  }
}

